import { Box, Image, HStack } from '@chakra-ui/react';
import React, { useState, useEffect } from "react";
function Slideshow() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const slides = [
    { image: 'data:image/jpeg;base64,', alt: 'Image 1' },
    { image: 'data:image/jpeg;base64,', alt: 'Image 2' },
    { image: 'data:image/jpeg;base64,', alt: 'Image 3' },
  ];

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentSlide((currentSlide + 1) % slides.length);
    }, 5000); // 5 seconds

    return () => clearInterval(intervalId);
  }, [currentSlide, slides]);

  return (
    <Box ml={"8%"} position="relative" mb={"2%"} w="1000px" >
      {slides.map((slide, index) => (
        <Box
          key={index}
          position="absolute"
          top="0"
          left="0"
          w="100%"
          // h="60%"
          opacity={index === currentSlide ? 1 : 0}
          transition="opacity 0.5s"
        >
          <Image w={"57%"} h={"40%"} src={slide.image} alt={slide.alt} />
        </Box>
      ))}
    </Box>
  );
}
export default Slideshow;